mapper.select:
SELECT ri.id, ri.code, ri.content, ri.images, ri.worker, ri.repair_type, ri.repair_status AS repairStatus, ri.create_date AS createDate, oi.name, oi.phone, tu.LAST_LOGIN_TIME AS lastTime, hi.address, hi.number, hi.floor, wi.name AS workerName, wi.phone AS workerPhone, wi.image AS workerImage FROM repair_info ri LEFT JOIN t_user tu ON ( tu.USER_ID = ri.user_id ) LEFT JOIN owner_info oi ON ( oi.user_id = tu.USER_ID ) LEFT JOIN houses_info hi ON ( hi.id = ri.houses_id ) LEFT JOIN worker_info wi ON ( wi.id = ri.worker ) WHERE 1 = 1
AND oi.name LIKE CONCAT('%',#{repairInfo.name},'%')
AND oi.phone LIKE CONCAT('%',#{repairInfo.phone},'%')
AND ri.repair_status = #{repairInfo.repairStatus}
AND ri.repair_type = #{repairInfo.repairType}
AND ri.create_date BETWEEN #{repairInfo.startDate} and #{repairInfo.endDate}
ORDER BY ri.create_date

select rt.id, rt.type_name, count(oi.id) as count from room_type rt left join room_info ri on (ri.`type` = rt.id) left join order_info oi on (oi.room_code = ri.code
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
) where 1 = 1 group by rt.id

SELECT wi.NAME, count( wi.id ) as num FROM repair_info ri LEFT JOIN worker_info wi ON ( wi.id = ri.worker ) WHERE 1 = 1
AND DATE_FORMAT(ri.create_date, '%Y') = #{year}
AND DATE_FORMAT(ri.create_date, '%c') = #{month}
GROUP BY wi.id

select tempDay.date, count(oi.id) as count from ( select date from ( select DATE_FORMAT(DATE_SUB(last_day(#{checkDate}), interval xc-1 day), '%Y-%m-%d') as date from ( select @xi := @xi + 1 as xc from ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc1, ( select 1 union select 2 union select 3 union select 4 union select 5 union select 6) xc2, ( select @xi := 0) xc0 ) xcxc) x0 where x0.date >= ( select date_add(#{checkDate}, interval-day(#{checkDate})+ 1 day))) as tempDay left join repair_info oi on (DATE_FORMAT( oi.create_date, '%Y-%m-%d' ) = DATE_FORMAT( tempDay.date, '%Y-%m-%d')
AND DATE_FORMAT(oi.create_date, '%Y') = #{year}
AND DATE_FORMAT(oi.create_date, '%c') = #{month}
) where 1 = 1 group by tempDay.date
